<?phpif (!defined('BASEPATH'))    exit('No direct script access allowed');class Contacto_model extends Back_Model {    public function __construct() {        parent::__construct();        $this->_table = 'contacto';        $this->_keyfield = 'id_contacto';        $this->_fields = "*";    }    public function get_all($where = array()) {        return $this->db->select('a.*, b.plaza')                        ->from('contacto as a')                        ->join('cat_plaza as b', 'a.id_plaza = b.id_plaza', 'left')                        ->get()->result();    }    public function get_contact($where = array()) {        $this->db->where('id_plaza', $where['id_plaza']);        return $this->db->select('*')->from('contacto')->get()->row();    }    public function get_plaza() {        return $this->db->select('id_plaza,plaza')->from('cat_plaza')->get()->result_array();    }    /**     * Retorna datos de la direcciÃ³n de la propiedad     * @param type $id_plaza     * @return type     */    public function get_location($id_plaza = null) {        $this->db->where('id_plaza', $id_plaza);        return $this->db->select('lat,lng')->from('contacto')->limit(1)->get()->row_array();    }}