<?phpif (!defined('BASEPATH'))    exit('No direct access allowed');class Hoteles extends Front_Controller {    public function __construct() {        parent::__construct();        $this->load->model('hoteles_model');        $this->_model = & $this->hoteles_model;        $this->load->library('form_validation');        $this->load->helper('toolkit');       // $this->load->library('text');        $this->_init();    }    public function index() {        $this->template->set('rs', $this->_model->get_hotels());        $this->_render();    }}